// <auto-generated />
using System;
using Kurisu.RemoteCall.Attributes.HelpMethods;
using Xunit;

namespace Kurisu.Test.RemoteCall.Generated
{
    public class PostAttribute_Additional_Tests
    {
        [Fact]
        public void HelpMethods_PostAttribute_DefaultCtor_Works()
        {
            var p = new PostAttribute();
            // default template empty
            Assert.Equal(string.Empty, p.Template);
            Assert.Equal(System.Net.Http.HttpMethod.Post, p.HttpMethod);
            Assert.Equal("application/json", p.ContentType);
            Assert.False(p.AsUrlencodedFormat);
        }

        [Fact]
        public void HelpMethods_PostAttribute_AsUrlencodedFlag_Honored()
        {
            var p = new PostAttribute("t", "application/json", true);
            Assert.Equal("t", p.Template);
            Assert.Equal("application/json", p.ContentType);
            Assert.True(p.AsUrlencodedFormat);

            var p2 = new PostAttribute("t2", "application/x-www-form-urlencoded", false);
            // content type forces AsUrlencodedFormat true
            Assert.Equal("application/x-www-form-urlencoded", p2.ContentType);
            Assert.True(p2.AsUrlencodedFormat);

            var p3 = new PostAttribute("t3", "text/plain", false);
            Assert.Equal("text/plain", p3.ContentType);
            Assert.False(p3.AsUrlencodedFormat);
        }

    }
}
